def my_collate_fn(features, return_tensors="pt"):
    def select_columns(xs, columns):
        result = []
        for x in xs:
            result.append({k: x[k] for k in columns})
        return result

    def make_mask(batch, feature):
        mask = torch.zeros_like(batch['input_ids'])
        for i, xs in enumerate(features):
            mask[i][xs[feature]] = 1
        return mask

    inputs = select_columns(features, ['input_ids', 'attention_mask', 'token_type_ids'])
    labels = select_columns(features, ['sent1_target_tokens_indexes', 'sent2_target_tokens_indexes'])
    batch = data_collator(inputs)
    batch['target_1_mask'] = make_mask(batch, 'sent1_target_tokens_indexes')
    batch['target_2_mask'] = make_mask(batch, 'sent2_target_tokens_indexes')

    return batch

def make_target_mask(example):
    target_1_positions = example['sent1_target_tokens_indexes']
    target_2_positions = example['sent2_target_tokens_indexes']
    target_mask = torch.zeros(len(example['input_ids']), dtype=int)
    target_mask[target_1_positions] = 1
    target_mask[target_2_positions] = 2
    return {**example, "target_mask": target_mask}

